function getCookie(e){var n="; "+document.cookie,t=n.split("; "+e+"=");if(2==t.length)return t.pop().split(";").shift()}App.room=App.cable.subscriptions.create("ConversationChannel",{connected:function(){},disconnected:function(){},received:function(e){e.author_id==getCookie("userId")?$(".chat").append(e.my_message):$(".chat").append(e.their_message)},speak:function(e){return this.perform("speak",{message:e})}}),$(document).on("submit","form[id=new_message]",function(e){e.preventDefault(),console.log("listening for submit....");var n={},t=$(this).serializeArray();$.each(t,function(){n[this.name]=this.value}),App.room.speak(n),$("#btn-input").val("")});