$(document).ready(function(){applyFormListener(),selectAllLevelsListener(),console.log("listening for form changes")});var applyFormListener=function(){$("#instructor_username").change(function(e){e.preventDefault();var c=$("#instructor_first_name").val(),n=$("#instructor_last_name").val(),t=$("#instructor_username").val();console.log("A user has entered their "+c+" "+n+" as their name and "+t+" as their email in the application form.");var a=$.ajax({url:"/notify_admin",type:"POST",data:{first_name:c,last_name:n,email:t}});a.done(function(e){console.log(e),console.log("successfully captured the email for "+e.email+" via ajax")})})},selectAllLevelsListener=function(){$("#selectAllSkiLevels").click(function(){this.checked?$(".ski-checkbox").each(function(){this.checked=!0}):$(".ski-checkbox").each(function(){this.checked=!1})}),$("#selectAllSnowboardLevels").click(function(){this.checked?$(".snowboard-checkbox").each(function(){this.checked=!0}):$(".snowboard-checkbox").each(function(){this.checked=!1})})};